"use strict";/** * @param name * @param value * @param days */const create = function (name, value, days) {    let expires, date;    if (days) {        date = new Date();        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));        expires = "; expires=" + date.toUTCString();    } else {        expires = "";    }    document.cookie = name + "=" + value + expires + "; path=/";};/** * @param name * @returns {string|null} */const read = function (name) {    let nameEQ = name + "=", ca = document.cookie.split(';');    for (let i = 0; i < ca.length; i++) {        let c = ca[i];        while (c.charAt(0) === ' ') c = c.substring(1, c.length);        if (c.indexOf(nameEQ) === 0) return c.substring(nameEQ.length, c.length);    }    return null;};const erase = function (name) {    create(name, "", -1);};export {create, read, erase}